import React from 'react';
import styles from'./Post.module.css'

const Post = (props) => {
  
  let addLike = (e) => {
    props.addLike(props);


    let newLike = document.createElement('i');
    newLike.setAttribute("onClick", "addLike");
    newLike.setAttribute("className", "fas fa-heart");
    console.log(newLike);
  }
      
  return(
    <div className={styles.post}> 
      <img src='https://clraik.com/forum/customavatars/avatar14341_15.gif' className={styles.avatar}/>
      <p>{props.text}</p>
      <i onClick={addLike} className="far fa-heart"></i><span className={styles.likes}>{props.likes}</span>
    </div>
  )
}

export default Post;
