import React from 'react';
import styles from './Messages.module.css';
import DialogueWindow from './DialogueWindow/DialogueWindow';
import { NavLink } from 'react-router-dom';
import { Route } from 'react-router-dom';

const DialogName = (props) => {
  return (
  <li><NavLink activeClassName={styles.active} to={'/messages/'+ props.id}>{props.name}</NavLink></li>
  )

}

const Messages = (props) => {

  let interlocutorName = props.messages.dialogs.map(item => <DialogName name={item.name} id={item.id}/>);

  return (
      <div>
        <h4 className={styles.headline}>Messages</h4>
        <div className={styles.messages}>
            <ul className={styles.dialogs}>
                {interlocutorName}
            </ul>
            <Route path='/messages/1'><DialogueWindow messages={props.messages.messages}/></Route>
            <Route path='/messages/2'><DialogueWindow messages={props.messages.messages}/></Route>
        </div>
      </div>
    )
  }

export default Messages;
