import React from 'react';
import styles from './Botton.module.css'

export const BottonToogleFollow = (action, followed, toogleActive, xex, id) => {
    let result = xex.some((item) => {
        return item == 4;
    })
    console.log(result,id);
    return followed == true ? <button onClick={action} className={result ? styles.disable : styles.buttonUnfollow} disabled={toogleActive}>unfollow</button> :
        <button onClick={action} className={toogleActive ? styles.disable : styles.buttonFollow} disabled={toogleActive}>follow</button>
}
