import React from 'react';
import styles from './Botton.module.css'

export const BottonToogleFollow = (action, followed, arr, id) => {
    let result = arr.some((item) => {
        return item == id;
    })
    return followed == true ? <button onClick={action} className={result ? styles.disable : styles.buttonUnfollow} disabled={result}>{result ? 'processing':'unfollow'}</button> :
        <button onClick={action} className={result ? styles.disable : styles.buttonFollow} disabled={result}>follow</button>
}
