import axios from 'axios';

const urlRequest = 'https://social-network.samuraijs.com/api/1.0';

export const apiGetUsers = (count, page) => {
    return axios.get(`${urlRequest}/users?count=${count}&page=${page}`, {
        withCredentials: true,
    }).then((response) => {
        return response.data
    });
};

export const apiAuth = () => {
    return axios.get(`${urlRequest}/auth/me`, {
        withCredentials: true,
    }).then((response) => {
        return response.data
    });
};

export const apiSetProfile = (userId) => {
    return axios.get(`${urlRequest}/profile/${userId}`)
        .then((response) => {
            return response.data
        });
};

export const apiFollowUser = (id) => {
    return axios.post(`${urlRequest}/follow/${id}`, {}, {
        withCredentials: true,
        headers: {
            'API-KEY': '1f53d161-bcd3-47d8-8414-bda7e59dbf15',
        }
    });
};

export const apiUnfollowUser = (id) => {
    return axios.delete(`${urlRequest}/follow/${id}`, {}, {
        withCredentials: true,
        headers: {
            'API-KEY': '1f53d161-bcd3-47d8-8414-bda7e59dbf15',
        }
    });
};
