import React from 'react';
import styles from './ProfileInfo.module.css'

class ProfileStatus extends React.Component {


    state = {
        editMode: false,
        statusText: 'Введите Ваш первый статус',
    }

    onEditMode() {
        this.setState({
            editMode: true
        })
    }
    offEditMode(e) {
        this.setState({
            editMode: false,
            statusText: e.target.value,
        })
        console.log(e);
    }

    render() {
        return (
            <div>{this.state.editMode == true ?
                <div>
                    <input autoFocus={true} type="text" />
                    <button onBlur={this.offEditMode.bind(this)}>Опубликовать</button>
                </div> :
                <div>
                    <span className={styles.status} onClick={this.onEditMode.bind(this)}>{this.state.statusText}</span>
                </div>}
            </div>
        )
    }
}

export default ProfileStatus
