import React from 'react';
import styles from './ProfileInfo.module.css'
import { useState } from 'react';

const ProfileStatusWithHooks = (props) => {

    let [editMode, setEditMode] = useState(false)
    let [statusText, setStatusText] = useState(props.status)

    let enableMode = () => {
        setEditMode(true);
    }
    let disableMode = () => {
        setEditMode(false);
    }

    return (
        <div>{editMode == true ?
            <div className={styles.statusEditOn}>
                <input placeholder="max-60 symbol" maxLength={60} autoFocus={true} type="text" onBlur={disableMode}/>
                <button onClick={disableMode}><i className="fas fa-arrow-circle-right"></i></button>
            </div> :
            <div className={styles.statusEditOff}>
                <span onClick={enableMode}> {statusText} =(</span>
            </div>}
        </div>
    )
}

export default ProfileStatusWithHooks
