import React from 'react';
import styles from './ProfileInfo.module.css'
import { useState } from 'react';

const ProfileStatusWithHooks = (props) => {

    let stateWithSetState = useState(false);
    let editMode = stateWithSetState[0];

    console.log(stateWithSetState)

    return (
        <div>{editMode == true ?
            <div className={styles.statusEditOn}>
                <input placeholder="max-60 symbol" maxLength={60} autoFocus={true} type="text"/>
                <button ><i className="fas fa-arrow-circle-right"></i></button>
            </div> :
            <div className={styles.statusEditOff}>
                <span >Статус пуст =(</span>
            </div>}
        </div>
    )
}

export default ProfileStatusWithHooks
