import React from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import Header from './Header';
import { setUserDataAC, toogleIfFetchingAC } from '../../redux/authReducer';

class HeaderContainer extends React.Component {

  componentDidMount() {
    let serverRequest = axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`, {
      withCredentials: true,
    });
    let handler = (response) => {
      if (response.data.resultCode === 0) {
        this.props.setUserData(response.data.data)
      }
    };
    serverRequest.then(handler);
    console.log(this.props)
  }
  render() {
    return (<Header />)
  }
}


let mapStateToProps = (state) => {
  return {
    userInfo: state.auth
  }
}
let mapDispatchToProps = {
  setUserData: setUserDataAC,
  toogleIfFetching: toogleIfFetchingAC
}

export default connect(mapStateToProps, mapDispatchToProps)(HeaderContainer);
