import React from 'react';
import styles from './DialogWithPerson.module.css';
import { NavLink } from 'react-router-dom';


const DialogWithPerson = (props) => {
  return (
  <li>
    <NavLink className={styles.person} activeClassName={styles.active} to={'/messages/'+ props.id}>
      <img className={styles.avatar} src="https://plastok.com.ua/images/reviews/1579759589_inbound5909117393130304794.jpg" alt=""/>
      <span>{props.name}</span>
    </NavLink>
  </li>
  )

}
export default DialogWithPerson;
