export const required = (value) => {
    if (!value) return console.log('вы ввели 0 символов');

    console.log(value);

}

export const maxLength = (max) => (value) => {
    console.log(value)
    if (value && value.length > max) {
        console.log(`Must be ${max} characters or less`)
    }
    else { console.log(undefined) }
};
