import React from 'react';

class ProfileStatus extends React.Component {


    state = {
        editMode: false,
    }



    toogleEditMode() {
        this.setState ({
            editMode:true
        })
    }



    render() {
        return (
            <div>{this.state.editMode == true ?
                <div>
                    <input type="text" value='' />
                </div> :
                <div>
                    <span onClick={this.toogleEditMode.bind(this)}>Спанчик</span>
                </div>}
            </div>
        )
    }
}

export default ProfileStatus
