import React from 'react';
import styles from './Messages.module.css';
import Dialog from './Dialog/Dialog';
import DialogMe from './DialogMe/DialogMe';
import { NavLink } from 'react-router-dom';

function Messages() {
    return (
      <div>
        <h4 className={styles.headline}>Messages</h4>
        <div className={styles.messages}>
            <ul className={styles.dialogs}>
              <NavLink to='/messages/andrey'><li>Andrey</li></NavLink>
              <NavLink to='/messages/andrey'><li>Shaha</li></NavLink>
              <NavLink to='/messages/andrey'><li>Vlad</li></NavLink>
              <NavLink to='/messages/andrey'><li>Denis</li></NavLink>
              <NavLink to='/messages/andrey'><li>Daniil</li></NavLink>
              <NavLink to='/messages/andrey'><li>Oleg</li></NavLink>
            </ul>
          <div className={styles.allMessages}> 
            <Dialog name='Andrey' text='Hello!'/>
            <Dialog name='Andrey' text='How are you?'/>
            <DialogMe name='Me' text='I`m ok, and you?'/>
            <Dialog name='Andrey' text='I`m fine!'/>
            <div className={styles.chatInput}>
              <textarea placeholder='new message'></textarea>
              <button><i class="far fa-arrow-alt-circle-right"></i></button>
            </div>
          </div>   
        </div>
      </div>
    )
  }

export default Messages;
