import React from 'react';
import styles from './Users.module.css'
import User from './User/User';
import axios from 'axios';

const Users = (props) => {
    if (props.users.length === 0) {

        let server = axios.get('https://social-network.samuraijs.com/api/1.0/users');

        let serverAction = (response) => {
            props.setUsers(response.data.items);
        };
        server.then(serverAction);
    }
    let userDate = props.users.map((item) => {
        return (item.followed == true) ?
            <User name={item.name} key={item.id} id={item.id} followed='follow' toogleFollow={props.toogleFollow} /> :
            <User name={item.name} key={item.id} id={item.id} followed='unfollow' toogleFollow={props.toogleFollow} />
    });

    return (
        <div className={styles.users}>
            <h3 className={styles.tittle}>Users</h3>
            {userDate}
            <button onClick={console.log('qfs')} className={styles.button}><i className="fas fa-long-arrow-alt-down"></i>More users<i className="fas fa-long-arrow-alt-down"></i></button>
        </div>
    )
}

export default Users;
