import React from 'react';
import { NavLink } from 'react-router-dom';
import styles from './User.module.css'
import axios from 'axios';

const User = (props) => {
    let onToogleFollow = (e) => {
        props.toogleFollow(props.id, props.followed);
        if (props.followed == false) {
            alert('Подписаля')
            let serverRequest = axios.get(`https://social-network.samuraijs.com/api/1.0/follow/${props.id}`, {
                withCredentials: true,
            });
            let handler = (response) => {
                console.log(response)
            };
            serverRequest.then(handler);
        } else {
            alert('Отписался')
            let serverRequest = axios.get(`https://social-network.samuraijs.com/api/1.0/follow/${props.id}`, {
                withCredentials: true,
            });
            let handler = (response) => {
                console.log(response)
            };
            serverRequest.then(handler);
        }
    }
    return (
        <div className={styles.user}>
            <div className={styles.user_logo}>
                <NavLink to={`/profile/${props.id}`}>
                    <img src={props.avatar} alt="" className={styles.avatar} />
                </NavLink>
                {props.followed == true ? <button onClick={onToogleFollow} className={styles.button}>unfollow</button> :
                    <button onClick={onToogleFollow} className={styles.button}>follow</button>}
            </div>
            <div className={styles.user_info}>
                <p className={styles.name}>{props.name}</p>
                <p className={styles.status}>{props.status}</p>
            </div>
        </div>
    )
}

export default User;
