import React from 'react';
import { Field } from 'redux-form';
import styles from './AddNewText.module.css';

const FormAddNewText = (props, name, placeholder) => {
    return (
        <form onSubmit={props.handleSubmit, props.id}>
          <div>
            <Field className={styles.inputText} name={"newMessage"} component="textarea" type="text" placeholder="How are you?" />
          </div>
          <div>
            <button className={styles.inputSubmit}>Send</button>
          </div>
        </form>
    )
}

export default FormAddNewText
