let AuthRedirectComponent = connectedRedirectComponent(Messages) // High Order Component (hoc)

let mapStateToProps = (state) => {
  return {
    friendsPage: state.friendsPage,
  }
}

let mapDispatcherToProps = (dispatch) => {
  return {
    addMessage: (text,id) => {dispatch(addMessageActionCreator(text,id))}
  }
}



export default connect(mapStateToProps,mapDispatcherToProps)(AuthRedirectComponent);
