import React from 'react';
import styles from './User.module';

const User = (props) => {
    let followAndUnfollow = () => {
        (props.followed == "follow") ? props.follow(props.id) : props.unFollow(props.id);
    }
    
    return (
        <div>
            <h3>{props.name}</h3>
            <button onClick={followAndUnfollow}>{props.followed}</button>
        </div>
    )
}

export default User;
