import React from 'react';
import styles from './Botton.module.css'

export const BottonToogleFollow = (action, followed, toogleActive) => {
    console.log(toogleActive);
    return followed == true ? <button onClick={action} className={toogleActive ? styles.disable : styles.buttonUnfollow} disabled={toogleActive}>unfollow</button> :
        <button onClick={action} className={toogleActive ? styles.disable : styles.buttonFollow} disabled={toogleActive}>follow</button>
}
