export const toogleFollowAC = (id, followed, target) => {
  return {
    type: "TOOGLE_FOLLOW",
    userId: id,
    followedText: followed,
    targetButton: target
  }
}
export const setUsersAC = (users, totalCount) => {
  return {
    type: 'SET_USERS',
    allUsers: users,
    total: totalCount
  }
}
export const setPageAC = (clickPage) => {
  return {
    type: 'SET_PAGE',
    clickPage,
}
}
let initialState = {
  users: [],
  pageSize: 100,
  totalUsersCount: 2,
  activePage: 5,
};

const UsersReducer = (state = initialState, action) => {
  let stateCopy;

  switch (action.type) {
    case "TOOGLE_FOLLOW":
      stateCopy = {
        ...state,
        users: [...state.users]
      };
      for (let k of stateCopy.users) {
        if (k.id == action.userId) {
          (action.followedText == 'follow') ? k.followed = false : k.followed = true;
        }
      }
      return stateCopy;
    case 'SET_USERS':
      stateCopy = {
        ...state,
        users: [...state.users, ...action.allUsers,],
        totalUsersCount: action.total,
      }
      return stateCopy;
    case 'SET_PAGE':
      stateCopy = {
        ...state,
        activePage: action.clickPage,
      }
      return stateCopy;
    default:
      return state;
  }
}

export default UsersReducer;
